class IconInput(WidgetWithScript, forms.widgets.Input):
    """Widget to select Font Awesome icon."""
    template_name = 'waggylabs/widgets/icon_input.html'
    
    def __init__(
        self,
        icons=(settings.WAGGYLABS_FONTAWESOME_ICONS if
               hasattr(settings, 'WAGGYLABS_FONTAWESOME_ICONS')
               else DEFAULT_FONTAWESOME_ICONS),
        attrs={
            'placeholder': 'Icon - start typing'
        }):
        self.icons_json = json.dumps(icons)
        super().__init__(attrs)
        
    def get_context(self, name, value, attrs):
        context = super().get_context(name, value, attrs)
        context['widget']['icons_json'] = self.icons_json
        context['widget']['icons_json_id'] = 'id-' + random_uuid()
        return context
    
    @property
    def media(self):
        return forms.Media(
            css={
                "all": (
                    "fontawesomefree/css/fontawesome.css",
                    "fontawesomefree/css/brands.css",
                    "fontawesomefree/css/regular.css",
                    "fontawesomefree/css/solid.css",
                    )
            },
            js=(
                # Wagtail already uses jquery-ui.js so no need to add here
            )
        )
        
class IconInputAdapter(WidgetAdapter):
    """Replaces the textarea with MathTextareaAdapter to have Javascript code to use CodeMirror for editing and MathJax for preview."""
    js_constructor = "waggylabs.widgets.IconInput"

    class Media:
        js = ["waggylabs/js/blocks/icon-input-adapter.js"]

register(IconInputAdapter(), IconInput)